        public static int mapHeight;
        public static int mapWidth;

        static char[] walkables = new char[] { ' ', '▒', '▀', '┼' };
        public void DrawMap(char[,] map)
        {
            Console.Clear();
            mapHeight = map.GetLength(0);
            mapWidth = map.GetLength(1);

            for (int mapX = 0; mapX < mapHeight; mapX++)
            {
                Console.SetCursorPosition(2, mapX + 1);
                for (int mapY = 0; mapY < mapWidth; mapY++)
                {
                    char tile = map[mapX, mapY];

                    DrawTile(tile);

                    Console.ResetColor();
                }

            }
        }

        public static void DrawTile(char tile)
        {
            switch (tile)
            {
                case '█':
                case '▄':
                    Random rand = new Random();
                    int roll = rand.Next(1, 3);
                    if (roll == 1)
                    {
                        Console.ForegroundColor = ConsoleColor.DarkGray;
                    }
                    if (roll == 2)
                    {
                        Console.ForegroundColor = ConsoleColor.Gray;
                    }
                    Console.Write(tile);
                    break;
                case '▀':
                    //Console.BackgroundColor = ConsoleColor.Green;
                    Console.ForegroundColor = ConsoleColor.Blue;
                    Console.Write(tile);
                    break;

                case '▒':
                    Console.BackgroundColor = ConsoleColor.DarkYellow;
                    //Console.ForegroundColor = ConsoleColor.DarkBlue;
                    Console.Write(tile);
                    break;

                case '~':
                    Console.BackgroundColor = ConsoleColor.Blue;
                    Console.ForegroundColor = ConsoleColor.DarkBlue;
                    Console.Write(tile);
                    break;

                case '┼':
                    Console.BackgroundColor = ConsoleColor.DarkYellow;
                    Console.ForegroundColor = ConsoleColor.Red;
                    Console.Write(tile);
                    break;

                default:
                    Console.Write(tile);
                    break;

            }
            Console.ResetColor();
        }

        public static bool CheckWalkable(char destination)
        {
            bool goTime = false;

            foreach (char walkable in walkables)
            {
                if (destination == walkable)
                {
                    goTime = true;
                }
            }

            return goTime;
        }
    }
}
