        };

        static int mapHeight = map.GetLength(0);
        static int mapWidth = map.GetLength(1);

        static char[] walkables = new char[] { ' ', '▒', '▀', '┼' };
        public void DrawMap()
        {
            for (int mapX = 0; mapX < mapHeight; mapX++)
            {
                Console.SetCursorPosition(2, mapX + 1);
                for (int mapY = 0; mapY < mapWidth; mapY++)
                {
                    char tile = map[mapX, mapY];

                    DrawTile(tile);

                    Console.ResetColor();
                }

            }
        }

        public static void DrawTile(char tile)
        {
            switch (tile)
            {
                case '█':
                case '▄':
                case '▀':
                    //Console.BackgroundColor = ConsoleColor.Green;
                    Console.ForegroundColor = ConsoleColor.DarkGreen;
                    Console.Write(tile);
                    break;

                case '▒':
                    Console.BackgroundColor = ConsoleColor.DarkYellow;
                    //Console.ForegroundColor = ConsoleColor.DarkBlue;
                    Console.Write(tile);
                    break;

                case '~':
                    Console.BackgroundColor = ConsoleColor.Blue;
                    Console.ForegroundColor = ConsoleColor.DarkBlue;
                    Console.Write(tile);
                    break;

                case '┼':
                    Console.BackgroundColor = ConsoleColor.DarkYellow;
                    Console.ForegroundColor = ConsoleColor.Red;
                    Console.Write(tile);
                    break;

                default:
                    Console.Write(tile);
                    break;

            }
            Console.ResetColor();
        }
        public void PlayerDraw(int x, int y, char character)
        {
            Console.SetCursorPosition(x, y);
            Console.WriteLine(character);
        }

        public bool CheckWalkable(char destination)
        {
            bool goTime = false;

            foreach (char walkable in walkables)
            {
                if (destination == walkable)
                {
                    goTime = true;
                }
            }

            return goTime;
        }
    }
